import React, { Component } from "react";
import { Parallax } from "react-parallax";
import * as emailjs from "emailjs-com";

const image2 =
  "https://images.unsplash.com/photo-1612474341291-42f489fa11d3?ixid=MXwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHw%3D&ixlib=rb-1.2.1&auto=format&fit=crop&w=1650&q=80";

const firstNameRe = /[A-Z]{1}[a-z]{2,20}/g;
const lastNameRe = /[A-Z]{1}[a-z]{2,20}/g;
const emailRe = /^([a-zA-Z0-9_\-\.]+)@([a-zA-Z0-9_\-\.]+)\.([a-zA-Z]{2,5})$/;
const phoneRe = /[0-9]{10}/g

export default class ContactPage extends Component {
  constructor(props) {
    super(props);

    this.state = {
      showForm: true,
      firstname: "",
      firstnameError:'',
      lastname: "",
      lastnameError:"",
      message: "",
      email: "",
      emailError:"",
      phone: "",
      phoneError:"",
      sent: false,
      error: "",
      errorText:""
    };
    this.handleOnChange = this.handleOnChange.bind(this);
    this.onFormSubmit = this.onFormSubmit.bind(this);
    this.handleValidation = this.handleValidation.bind(this);
  }

  handleOnChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value,
    });
  };

  handleValidation = () => {
    let firstnameError = ''
    let lastnameError = ''
    let emailError = ''
    let phoneError = ''

    if(!firstNameRe.test(this.state.firstname)){
      firstnameError = "Invalid First Name"
      this.setState({ firstnameError })
    }

    if(!lastNameRe.test(this.state.lastname)){
      this.setState({
        lastnameError: 'Invalid Last Name'  })  
    }

    if(!emailRe.test(this.state.emailError)){
      this.setState({
        emailError: 'Invalid Email'
      })    
    }

    if(!phoneRe.test(this.state.phone)){
      this.setState({
        phoneError: 'Invalid Phone Number'
      })      
    }

    if(firstnameError.length>1 || lastnameError.length>1 || emailError.length>1 || phoneError.length>1){
      return false
    }
    return true

  }
  // handleLastName = (e) => {
  //   this.setState({
  //     lastname: e.target.value,
  //   });
  // };
  // handleMessage = (e) => {
  //   this.setState({
  //     message: e.target.value,
  //   });
  // };

  onFormSubmit = (e) => {
    e.preventDefault();

    const isValid = this.handleValidation();

    if (!isValid){
      console.log(this.state)

      this.setState({
        firstname: '',
        lastname: '',
        email: '',
        phone: ''
      })
    }
    else{
      emailjs
      .sendForm(
        "service_qmkim1s",
        "template_l9fzavc",
        e.target,
        "user_TidLj0qzWYcbKwfKAED9y"
      )
      .then(
        (result) => {
          console.log(result.text);
        },
        (error) => {
          console.log(error.text);
        }
      );
    e.target.reset();

    this.setState({
      firstname: "",
      lastname: "",
      message: "",
      email:"",
      phone:""
    });

    }


  };
  render() {
    return (
      <Parallax bgImage={image2} blur={{ min: -10, max: 10 }} strength={-300}>
        <div className="contact" id="contact">
          <div className="form-container">
            <form onSubmit={this.onFormSubmit}>
              <div className="name">
                <label className="input-label">First Name:</label>
                <input
                  onChange={this.handleOnChange}
                  className="name-input"
                  type="text"
                  placeholder="First Name"
                  name="firstname"
                  value={this.state.firstname}

                /> {this.state.firstnameError}

                <label className="input-label">Last Name:</label>
                <input
                  onChange={this.handleOnChange}
                  className="name-input"
                  type="text"
                  placeholder="Last Name"
                  name="lastname"
                  value={this.state.lastname}
                  errorText = {this.state.lastnameError}
                />

              </div>

              <div className='name2'>
                <label className="input-label">Email</label>
                <input
                  onChange={this.handleOnChange}
                  className="name-input"
                  type="email"
                  placeholder="Email"
                  name="email"
                  value={this.state.email}
                  errorText={this.state.emailError}
                />

                <label className="input-label">Ph No.</label>
                <input
                  onChange={this.handleOnChange}
                  className="name-input"
                  type="text"
                  placeholder="Phone number"
                  name="phone"
                  value={this.state.phone}
                  errorText={this.state.phoneError}
                />

              </div>

              <div className="msg">
                <label className="text-label">Message:</label>
                <textarea
                  onChange={this.handleOnChange}
                  className="text-input"
                  placeholder="Type your message here..."
                  name="message"
                  value={this.state.message}
                />
              </div>
              <div className="send-btn">
                <button type="submit" className="btn">
                  Send
                </button>
              </div>
            </form>
          </div>
        </div>
      </Parallax>
    );
  }
}
