import React, { Component } from "react";
import { Parallax } from "react-parallax";
import * as emailjs from 'emailjs-com';

const image2 =
  "https://images.unsplash.com/photo-1612474341291-42f489fa11d3?ixid=MXwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHw%3D&ixlib=rb-1.2.1&auto=format&fit=crop&w=1650&q=80";

export default class ContactPage extends Component {
  constructor(props) {
    super(props);

    this.state = {
      showForm: true,
      firstname: '',
      lastname: '',
      message: '',
      sent: false,
      error: ''

    };
    this.handleFirstName = this.handleFirstName.bind(this);
    this.handleLastName = this.handleLastName.bind(this);
    this.handleMessage = this.handleMessage.bind(this);
    this.onFormSubmit = this.onFormSubmit.bind(this);
  }

  handleFirstName = (e) => {
      this.setState({
          firstname: e.target.value

      })
  }
  handleLastName = (e) => {
      this.setState({
          lastname: e.target.value
      })
  }
  handleMessage = (e) => {
      this.setState({
          message: e.target.value
      })
  }

  onFormSubmit = (e) => {
      e.preventDefault();

      emailjs.sendForm(
        'service_qmkim1s',
        'template_l9fzavc',
        e.target,
        'user_TidLj0qzWYcbKwfKAED9y'
      )
      .then(
        result => {
          console.log(result.text)
        },
        error => {
          console.log(error.text);
        },
      );
      e.target.reset();

      this.setState({
        firstname: '',
        lastname: '',
        message: '',

      })
  }
  render() {
    return (
      <Parallax bgImage={image2} blur={{ min: -10, max: 10 }} strength={-300}>
        <div className="contact" id="contact">
          <div className="form-container">
            <form onSubmit={this.onFormSubmit}>
              <div className="name">
                <label className="input-label">First Name:</label>
                <input
                  onChange={this.handleFirstName}
                  className="name-input"
                  type="text"
                  placeholder="First Name"
                  name='firstname'
                  value={this.state.firstname}
                />
                <label className="input-label">Last Name:</label>
                <input
                  onChange={this.handleLastName}
                  className="name-input"
                  type="text"
                  placeholder="Last Name"
                  name='lastname'
                  value={this.state.lastname}
                />
                <label className='input-label'>Email</label>
                <input onChange={this.handleOnChange} className='name-input' type='email' placeholder='Email' />
              </div>

              <div className="msg">
                <label className="text-label">Message:</label>
                <textarea
                  onChange={this.handleMessage}
                  className="text-input"
                  placeholder="Type your message here..."
                  name='message'
                  value={this.state.message}
                />
              </div>
              <div className="send-btn">
                <button type='submit' className="btn">Send</button>
              </div>
            </form>
          </div>
        </div>
      </Parallax>
    );
  }
}

